import logo from './logo.svg';
import { Component } from 'react'
import './App.css';
import web3 from './web3'
import ballot from './ballot'

class App extends Component {

  state = {
     admin: '',
     message:''

  };

  async componentDidMount (){
    const admin = await ballot.methods.admin().call(); 
    // const players = await lottery.methods.getPlayers().call(); 
    // const balance = await web3.eth.getBalance(lottery.options.address); 

    this.setState({ admin });
  }

  onSubmit = async (event) => {
    event.preventDefault();

    const accounts = await web3.eth.getAccounts();


    this.setState({ message: 'Waiting on transcation success...'});

    await ballot.methods.vote().send({
      from: accounts[0],
      value: web3.utils.toWei(this.state.value , 'ether'),
      arguments: [0]
    })

    this.setState({ message: 'You have been entered!'});

  };

  pickWinner = async (event) => {
    event.preventDefault();

    const accounts = await web3.eth.getAccounts();

    this.setState({ PickingWinnerMessage: 'Picking Winner...'});


    await ballot.methods.pickWinner().send({
      from: accounts[0]
    })

    this.setState({ PickingWinnerMessage: 'Winner has been picked'});

  }

  render() {

    // console.log(web3.version)
    //web3.eth.getAccounts().then(console.log);

    return (
      <div>
        <h2>Lottery Contract</h2>
        <p>
          This contract is managed by {this.state.manager}.  
          There are currently {this.state.players.length} people eneterd, 
          competing to win {web3.utils.fromWei(this.state.balance, 'ether')} ether!  
        </p>
        <form onSubmit={this.onSubmit}>
          <h4>Want to try your luck?</h4> 
          <div>
            <label>Amount of ether to enter</label>
            <input 
              value={this.state.value}
              onChange={event => this.setState({ value: event.target.value})}
            />
          </div>
          <button>Enter</button>         
        </form>

        <h2>{this.state.message}</h2>

        <form onSubmit={this.pickWinner}>
          <h4>Time to pick a winner?</h4>
          <button>
            Pick Winner
          </button>
          <h4>{this.PickingWinnerMessage}</h4>
        </form>
      </div>

    );
  }
}

export default App;
