import logo from './logo.svg';
import { Component } from 'react'
import './App.css';
import web3 from './web3'
import ballot from './ballot'

class App extends Component {

  state = {
     admin: '',
     message:'',
     value:0,
     index:0
  };

  async componentDidMount (){
    const admin = await ballot.methods.admin().call(); 

    // const players = await lottery.methods.getPlayers().call(); 
    // const balance = await web3.eth.getBalance(lottery.options.address); 

    this.setState({ admin });
  }

  onSubmit = async (event) => {
    event.preventDefault();

    const accounts = await web3.eth.getAccounts();

    this.setState({ message: 'Waiting on transcation success...'});

    await ballot.methods.vote(this.state.value).send({
      from: accounts[0],
      gas:'100000'
    })

    this.setState({ message: 'You have been entered!'});
  };

  // pickWinner = async (event) => {
  //   event.preventDefault();

  //   const accounts = await web3.eth.getAccounts();

  //   this.setState({ PickingWinnerMessage: 'Picking Winner...'});


  //   await ballot.methods.pickWinner().send({
  //     from: accounts[0]
  //   })

  //   this.setState({ PickingWinnerMessage: 'Winner has been picked'});

  // }

  render() {
    return (
      <div>
        <h2>Ballot Contract</h2>
        <p>
          This contract is managed by {this.state.admin}.  
        </p>
        <hr/>
        <form onSubmit={this.onSubmit}>
          <h4>Please choose one candidate! </h4> 
          <div>
            {/* <input 
              value={this.state.value}
              onChange={event => this.setState({ value: event.target.value})}
            /> */}


            <div onChange={event => this.setState({index: event.target.value})}>
              <label><input type="radio" name="candidate" value="0"/>Ahmed</label>
              <label><input type="radio" name="candidate" value="1"/>Joe</label>
              <label><input type="radio" name="candidate" value="2"/>Khedr</label>
              <label><input type="radio" name="candidate" value="3"/>Ebola</label>
              <label><input type="radio" name="candidate" value="4"/>Martini</label>
            </div>

          <label>
            Is going:
            <input
              name="isGoing"
              type="checkbox"
              onChange={this.handleInputChange} />
          </label>

          </div>
          <button>Vote</button>         
        </form>

        <h2>{this.state.message}</h2>

        <form onSubmit={this.pickWinner}>
          <h4>Time to pick a winner?</h4>
          <button>
            Pick Winner
          </button>
          <h4>{this.PickingWinnerMessage}</h4>
        </form>
      </div>

    );
  }
}

export default App;
